"""A compilation of Rust solutions to Advent of Code 2022 Day 6, converted to Python.

The challenge is to find the first n-length isogram substring and return the index of the first character of the substring.

Source:
    https://www.youtube.com/watch?v=U16RnpV48KQ
"""


import timeit
import matplotlib.pyplot as plt


# 2458 is the index of the first 14-length isogram of the string below.
STRING = ""


def using_set(n, input):
    """Find the first n-length isogram substring.

    Args:
        n (int): The length of the isogram.
        input (str): The string to search from.

    Returns:
        int: The index of the isogram's first character
    """
    for i in range(len(input) - n + 1):
        substr = input[i : i + n]
        if len(set(substr)) == n:
            return i


def using_count(n, input):
    """Find the first n-length isogram substring.

    Args:
        n (int): The length of the isogram.
        input (str): The string to search from.

    Returns:
        int: The index of the isogram's first character
    """
    for i in range(len(input) - n + 1):
        substr = input[i : i + n]
        for char in substr:
            if substr.count(char) > 1:
                break
        else:
            return i


def lichtman_algorithm(n, input):
    """Find the first n-length isogram substring.

    Args:
        n (int): The length of the isogram.
        input (str): The string to search from.

    Returns:
        int: The index of the isogram's first character
    """
    filter = int()
    for char in input[: n - 1]:
        filter ^= 1 << (ord(char) % 32)
    for i in range(len(input) - n + 1):
        first_char = input[i]
        last_char = input[i + n - 1]
        filter ^= 1 << (ord(last_char) % 32)
        if filter.bit_count() == n:
            return i
        filter ^= 1 << (ord(first_char) % 32)


def perez_algorithm(n, input):
    """Find the first n-length isogram substring.

    Args:
        n (int): The length of the isogram.
        input (str): The string to search from.

    Returns:
        int: The index of the isogram's first character
    """
    i = 0
    while i + n <= len(input):
        state = int()
        pos = None
        substr = input[-len(input) - 1 + i + n : -len(input) - 1 + i : -1]

        for j, char in enumerate(substr):
            char_bit = 1 << (ord(char) % 32)
            if state & char_bit != 0:
                pos = -j + n - 1
                break
            state |= char_bit

        if pos is not None:
            i += pos
        elif state.bit_count() == n:
            return i

        i += 1


def main():
    assert using_set(14, STRING) == 2458
    assert using_count(14, STRING) == 2458
    assert lichtman_algorithm(14, STRING) == 2458
    assert perez_algorithm(14, STRING) == 2458

    number = 100
    function_data = {}
    function_data["Using set()"] = timeit.timeit(
        "using_set(14, STRING)", globals=globals(), number=number
    )
    function_data["Using count()"] = timeit.timeit(
        "using_count(14, STRING)", globals=globals(), number=number
    )
    function_data["Lichtman's algo"] = timeit.timeit(
        "lichtman_algorithm(14, STRING)", globals=globals(), number=number
    )
    function_data["Perez's algo"] = timeit.timeit(
        "perez_algorithm(14, STRING)", globals=globals(), number=number
    )

    plt.bar(function_data.keys(), function_data.values(), width=0.4)
    plt.show()


if __name__ == "__main__":
    main()
